import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import profilePic from './profile.png';
import es from './locales/es.json';
import en from './locales/en.json';

const Resume = styled.div`
    width: ${props => props.windowWidth < 1070 ? '100%' : '75%'};
    padding-left: 2rem;
    padding-right: 2rem;
    padding-top: 3.5rem;
    padding-bottom: 3.5rem;
    box-sizing: border-box;
    margin: auto;
    margin-top: 2.5%;
    margin-bottom: 2.5%;
    background-color: white;
    border-radius: 10px;
    box-shadow: 0px 0px 10px 0px grey;
    position: relative;
`;

const ProfilePic = styled.img`
    margin-top: -4.5rem;
    position: absolute;
    top: 0;
    right: 0;
    z-index: 0;
    transform: scale(0.5);
`;

const ShareButton = styled.button`
    display: block;
    width: 10rem;
    height: 50px;
    margin-top: 2.5rem;
    margin: 20px auto;
    background-color: #1a8cff;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1em;
    transition: background-color 0.3s ease;

    &:hover {
        background-color: #014d9f;
    }
`;

const LanguageToggle = styled.button`
    position: absolute;
    top: 10px;
    right: 10px;
    background-color: #1a8cff;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1em;
    transition: background-color 0.3s ease;

    &:hover {
        background-color: #014d9f;
    }
`;

const Contact = styled.ul`
    height: 11rem;
    font-size: 140%;
`;

const Portfolio = styled.p`
    font-size: 140%;
`;

const Underline = styled.span`
    text-decoration: underline;
`;

const Emoji = styled.span`
    font-size: 125%;
    margin-right: 0.5rem;
`;

function App() {
  const [language, setLanguage] = useState('es');
  const content = language === 'es' ? es : en;

  const toggleLanguage = () => {
    setLanguage(language === 'es' ? 'en' : 'es');
  };

  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const copyToClipboard = () => {
    const dummy = document.createElement('input');
    const text = window.location.href;
    document.body.appendChild(dummy);
    dummy.value = text;
    dummy.select();
    document.execCommand('copy');
    document.body.removeChild(dummy);
    alert("URL copiada al portapapeles!");
  };

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);

      if (window.innerWidth < 1070) {
        Array.from(document.getElementsByTagName("li")).forEach(function(li) {
          li.style.fontSize = "34px";
        });
        Array.from(document.getElementsByTagName("p")).forEach(function(p) {
          p.style.fontSize = "34px";
        });
        const resumeElement = document.getElementById("resume");
        if (resumeElement) {
          resumeElement.style.width = "100%";
        }
      } else {
        let count = 0;
        const interval = setInterval(()=>{
          if (count >= 360) {
            count = 0;
          }
          count += 1;
          document.body.style.background = "linear-gradient(" + count + "deg, lightpink, orange)";
        }, 50);
        return () => clearInterval(interval);
      }
    };


    handleResize();
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div>
      <LanguageToggle onClick={toggleLanguage}>
        {language === 'es' ? 'English' : 'Espa√±ol'}
      </LanguageToggle>
      <Resume windowWidth={windowWidth}>
        <div>
          <h1><Underline>{content.title}</Underline></h1>
          <h2>{content.position}</h2>
          <ProfilePic src={profilePic} alt="julio_cabanillas" />
        </div>
        <h3><Emoji>üéì</Emoji> <Underline>{content.education.title}</Underline></h3>
        <ul>
          {content.education.items.map((item, index) => <li key={index} dangerouslySetInnerHTML={{ __html: item }} />)}
        </ul>
        <h3><Emoji>üíª</Emoji> <Underline>{content.technicalSkills.title}</Underline></h3>
        <ul>
          {content.technicalSkills.items.map((item, index) => <li key={index}>{item}</li>)}
        </ul>
        <h3><Emoji>üë®‚Äçüíº</Emoji> <Underline>{content.experience.title}</Underline></h3>
        <ul>
          {content.experience.items.map((item, index) => <li key={index} dangerouslySetInnerHTML={{ __html: item }} />)}
        </ul>
        <h3><Emoji>üèÜ</Emoji> <Underline>{content.achievements.title}</Underline></h3>
        <ul>
          {content.achievements.items.map((item, index) => <li key={index} dangerouslySetInnerHTML={{ __html: item }} />)}
        </ul>
        <h3><Emoji>üåê</Emoji> <Underline>{content.languages.title}</Underline></h3>
        <ul>
          {content.languages.items.map((item, index) => <li key={index} dangerouslySetInnerHTML={{ __html: item }} />)}
        </ul>
        <h3><Emoji>üöó</Emoji> <Underline>{content.additionalSkills.title}</Underline></h3>
        <ul>
          {content.additionalSkills.items.map((item, index) => <li key={index}>{item}</li>)}
        </ul>
        <h3><Emoji>üéØ</Emoji> <Underline>{content.objective.title}</Underline></h3>
        <p>{content.objective}</p>
        <h3><Emoji>üìÇ</Emoji> <Underline>{content.portfolio.title}</Underline></h3>
        <Portfolio dangerouslySetInnerHTML={{ __html: content.portfolio }} />
        <h3><Emoji>üìû</Emoji> <Underline>{content.contact.title}</Underline></h3>
        <Contact>
          {content.contact.items.map((item, index) => <li key={index} dangerouslySetInnerHTML={{ __html: item }} />)}
        </Contact>
        <ShareButton onClick={copyToClipboard}>{content.shareButton}</ShareButton>
      </Resume>
    </div>
  );
}

export default App;
